class ModelContext {
    constructor(name) {
      this.name = name;
      this.timestamp = Date.now();
    }
  
    getContextName() {
      return this.name;
    }
  
    async fetchContextData() {
      console.log('Fetching data for context:', this.name);
      return Promise.resolve('Sample Context Data');
    }
  }
  
  class Model {
    constructor(name, context) {
      this.name = name;
      this.context = context;
    }
  
    getModelName() {
      return this.name;
    }
  
    async processModelData() {
      console.log(`Processing data for model: ${this.name} in context: ${this.context.getContextName()}`);
      const contextData = await this.context.fetchContextData();
      console.log('Context Data:', contextData);
      return 'Processed Model Data';
    }
  }
  
module.exports = {
    ModelContext,
    Model,
};
  